package jumpingstatements;

public class jumpingstatementdemo{

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		for(int a=1; a<=10; a++ )
		{
			if(a==7)
			{
				break;
			}
		System.out.println(a);
		}
		
	}
	//break = it stops at that condition until it becomes false (straight break)
	//continue= it will skip the condition/statement and get back into loop(skip)
	//loop is activated until it becomes false

}
